import "core-js/modules/es.array.push.js";
import search from "@/components/Search.vue";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
export default {
  components: {
    // Pagination,
    search
  },
  data() {
    return {
      status: true,
      tender: {
        state_tender_id: "",
        region: "state",
        country_id: "10",
        state_id: "",
        tender_no: "",
        title: "",
        description: "",
        state_agency_id: "",
        state_notice_id: "",
        opening_date: "",
        expiry_date: "",
        tender_url: "",
        fees: "",
        category_id: "",
        // place_of_performance: {
        //     city_name: '',
        //     state_name: '',
        //     country_name: ''
        // },
        state_office_address: {
          city: "",
          state: "",
          country: ""
        },
        primary_address: {
          title: "",
          full_name: "",
          email: "",
          phone: ""
        },
        secondary_address: {
          title: "",
          full_name: "",
          email: "",
          phone: ""
        },
        attachments: []
      },
      countries: [],
      states: [],
      notices: [],
      categories: [],
      agencies: [],
      errors: [],
      editor: ClassicEditor
    };
  },
  // mounted() {
  //     this.getCountries()
  //     if (this.tender.country_id) {
  //         this.getStates()
  //     }
  // },
  beforeRouteEnter(to, from, next) {
    next(vm => {
      vm.getCountries();
      if (vm.tender.country_id) {
        vm.getStates();
      }
      // vm.getRoles();
      if (to.name == "AddStateTender") {
        // vm.$refs.name.focus();
      } else {
        vm.status = false;
        let uri = {
          uri: "getStateTender",
          data: {
            state_tender_id: to.params.state_tender_id
          }
        };
        vm.$store.dispatch("post", uri).then(function (response) {
          console.log("response-----", response.data.data);
          vm.tender = response.data.data;
        }).catch(function (error) {
          vm.errors = error.response.data.errors;
          vm.$store.dispatch("error", error.response.data.message);
        });
      }
    });
  },
  methods: {
    handleFileUpload(event) {
      this.tender.attachments = event.target.files;
    },
    submitForm() {
      let vm = this;
      if (vm.status) {
        vm.addTender();
      } else {
        vm.updateTender();
      }
    },
    addTender() {
      let vm = this;
      let loader = vm.$loading.show();
      const formData = new FormData();
      for (var i = 0; i < vm.tender?.attachments?.length; i++) {
        formData.append("attachments[" + i + "]", vm.tender.attachments[i]);
      }
      formData.append("country_id", vm.tender.country_id);
      formData.append("state_id", vm.tender.state_id);
      formData.append("tender_no", vm.tender.tender_no);
      formData.append("title", vm.tender.title);
      formData.append("state_agency_id", vm.tender.state_agency_id);
      formData.append("state_notice_id", vm.tender.state_notice_id);
      formData.append("opening_date", vm.tender.opening_date);
      formData.append("expiry_date", vm.tender.expiry_date);
      formData.append("place_of_performance", JSON.stringify(vm.tender.place_of_performance));
      formData.append("state_office_address", JSON.stringify(vm.tender.state_office_address));
      formData.append("tender_url", vm.tender.tender_url);
      formData.append("fees", vm.tender.fees);
      formData.append("category_id", vm.tender.category_id);
      formData.append("description", vm.tender.description);
      formData.append("primary_address", JSON.stringify(vm.tender.primary_address));
      formData.append("secondary_address", JSON.stringify(vm.tender.secondary_address));
      vm.$store.dispatch("multipart_formdata", {
        uri: "addStateTender",
        data: formData
      }).then(() => {
        loader.hide();
        vm.discard();
        vm.$router.push("/state_tenders");
      }).catch(function (error) {
        loader.hide();
        vm.errors = error.response.data.errors;
        vm.$store.dispatch("error", error.response.data.message);
      });
    },
    updateTender() {
      let vm = this;
      let loader = vm.$loading.show();
      const formData = new FormData();
      for (var i = 0; i < vm.tender?.attachments?.length; i++) {
        formData.append("attachments[" + i + "]", vm.tender.attachments[i]);
      }
      formData.append("state_tender_id", vm.tender.state_tender_id);
      formData.append("country_id", vm.tender.country_id);
      formData.append("state_id", vm.tender.state_id);
      formData.append("tender_no", vm.tender.tender_no);
      formData.append("title", vm.tender.title);
      formData.append("state_agency_id", vm.tender.state_agency_id);
      formData.append("state_notice_id", vm.tender.state_notice_id);
      formData.append("opening_date", vm.tender.opening_date);
      formData.append("expiry_date", vm.tender.expiry_date);
      formData.append("place_of_performance", JSON.stringify(vm.tender.place_of_performance));
      formData.append("state_office_address", JSON.stringify(vm.tender.state_office_address));
      formData.append("tender_url", vm.tender.tender_url);
      formData.append("fees", vm.tender.fees);
      formData.append("category_id", vm.tender.category_id);
      formData.append("description", vm.tender.description);
      formData.append("primary_address", JSON.stringify(vm.tender.primary_address));
      formData.append("secondary_address", JSON.stringify(vm.tender.secondary_address));
      vm.$store.dispatch("multipart_formdata", {
        uri: "updateTenderState",
        data: formData
      }).then(() => {
        loader.hide();
        vm.discard();
        vm.$router.push("/state_tenders");
      }).catch(function (error) {
        loader.hide();
        vm.errors = error.response.data.errors;
        vm.$store.dispatch("error", error.response.data.message);
      });
    },
    discard() {
      this.tender.region = "state", this.tender.country_id = "10", this.tender.state_id = "", this.tender.tender_no = "", this.tender.title = "", this.tender.description = "", this.tender.state_agency_id = "", this.tender.state_notice_id = "", this.tender.opening_date = "", this.tender.expiry_date = "", this.tender.tender_url = "", this.tender.fees = "", this.tender.category_id = "", this.tender.place_of_performance = {
        city_name: "",
        state_name: "",
        country_name: ""
      }, this.tender.state_office_address = {
        city: "",
        state: "",
        country: ""
      }, this.tender.primary_address = {
        title: "",
        full_name: "",
        email: "",
        phone: ""
      }, this.tender.secondary_address = {
        title: "",
        full_name: "",
        email: "",
        phone: ""
      }, this.tender.attachments = [];
    },
    handleCountryChange(country) {
      this.tender.country_id = country;
      if (typeof this.tender.country_id === "number") {
        this.getStates();
      }
    },
    getCountries() {
      let vm = this;
      let loader = vm.$loading.show();
      vm.$store.dispatch("post", {
        uri: "getCountries"
      }).then(response => {
        loader.hide();
        vm.countries = response.data;
        vm.getStateNotices();
      }).catch(function (error) {
        loader.hide();
        vm.errors = error.response.data.errors;
        vm.$store.dispatch("error", error.response.data.message);
      });
    },
    getStates() {
      let vm = this;
      let loader = vm.$loading.show();
      vm.$store.dispatch("post", {
        uri: "getStates",
        data: vm.tender
      }).then(response => {
        loader.hide();
        vm.states = response.data;
      }).catch(function (error) {
        loader.hide();
        vm.errors = error.response.data.errors;
        vm.$store.dispatch("error", error.response.data.message);
      });
    },
    getStateNotices() {
      let vm = this;
      let loader = vm.$loading.show();
      vm.$store.dispatch("post", {
        uri: "getStateNotices",
        data: {
          meta: {
            region_id: 1
          }
        }
      }).then(response => {
        loader.hide();
        vm.notices = response.data;
        vm.getCategories();
      }).catch(function (error) {
        loader.hide();
        vm.errors = error.response.data.errors;
        vm.$store.dispatch("error", error.response.data.message);
      });
    },
    getCategories() {
      let vm = this;
      let loader = vm.$loading.show();
      vm.$store.dispatch("post", {
        uri: "getCategories"
      }).then(response => {
        loader.hide();
        vm.categories = response.data;
        vm.getStateAgencies();
      }).catch(function (error) {
        loader.hide();
        vm.errors = error.response.data.errors;
        vm.$store.dispatch("error", error.response.data.message);
      });
    },
    getStateAgencies() {
      let vm = this;
      vm.$store.dispatch("post", {
        uri: "getStateAgencies"
      }).then(response => {
        vm.agencies = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    }
  }
};
